import React, { useState, useRef, useContext, useEffect } from "react";
import Context from "../context/context";
import { createChart } from "lightweight-charts";
import { NavLink } from "react-router-dom";
import { Sparklines, SparklinesLine } from "react-sparklines";
import axios from "axios";
import "./coinrow.css";

export default function CoinRow(props) {
  const [sparklineColor, setSparklineColor] = useState();
  const { setSelectedCoinData, setCoinChartData } = useContext(Context);
  const isMounted = useRef(false);
  // console.log(props);
  // const { name, price, percentChange, data } = props.coin;
  const {
    rank,
    image,
    name,
    symbol,
    price,
    percentChange24hr,
    percentChange7d,
    marketCap,
    volume24hr,
    sparkline,
  } = props.coin;
  // console.log(sparkline);

  // const generateSparklineChartData = (sparklinePrices) => {
  //   // console.log(sparklinePrices);
  //   const sparklineDataArray = sparklinePrices.map((dataPoint, index) => {
  //     console.log({ time: index, value: dataPoint });
  //     return { time: index, value: dataPoint };
  //   });
  //   console.log(sparklineDataArray);
  // };

  // generateSparklineChartData(sparkline.price);

  // useEffect(() => {
  //   if (isMounted.current) {
  //     const chart = createChart(document.querySelector(".chart"), {
  //       width: 100,
  //       height: 75,
  //       rightPriceScale: {
  //         visible: false,
  //       },
  //       grid: {
  //         vertLines: {
  //           visible: false,
  //         },
  //         horzLines: {
  //           visible: false,
  //         },
  //       },
  //       crosshair: {
  //         vertLine: {
  //           visible: false,
  //         },
  //         horzLine: {
  //           visible: false,
  //         },
  //       },
  //       layout: {
  //         backgroundColor: "tan",
  //       },
  //       timeScale: { visible: false },
  //       // borderColor: "green",
  //       // timeVisible: false,
  //       // priceScaleMode: percentChange,
  //     });
  //     //fits timescale to the content
  //     chart.timeScale().fitContent();
  //     const lineSeries = chart
  //       .addLineSeries
  //       //used to set y-axis scale (not quite sure exactly how it works)
  //       //   {
  //       //   autoscaleInfoProvider: () => ({
  //       //     priceRange: {
  //       //       minValue: 0,
  //       //       maxValue: 100,
  //       //     },
  //       //   }),
  //       // }
  //       ();
  //     lineSeries.applyOptions({
  //       color: "red",

  //       lineWidth: 5,
  //       //crosshair dot
  //       crosshairMarkerVisible: false,
  //       //both of these are similar
  //       lastValueVisible: false,
  //       priceLineVisible: false,
  //       //can make the series disapper (labels remain)
  //       visible: true,
  //       //
  //     });
  //     // const customFormatter = (time, tickMarkType, locale) => {
  //     //   // tickMarkType: "year";
  //     // };
  //     lineSeries.setData(data);
  //     //   window.addEventListener("resize", handleResize);
  //   } else {
  //     isMounted.current = true;
  //   }
  // });

  const data = [
    { time: "2019-04-11", value: 80.01 },
    { time: "2019-04-12", value: 96.63 },
    { time: "2019-04-13", value: 76.64 },
    { time: "2019-04-14", value: 80.64 },
    { time: "2019-04-15", value: 40.64 },
    { time: "2019-04-16", value: 67.64 },
    { time: "2019-04-17", value: 57.64 },
  ];

  const retrieveDetailedChartData = (coin) => {
    console.log(`coin is ${coin}`);
    const coinLowercase = coin.charAt(0).toLowerCase() + coin.slice(1);
    console.log(coinLowercase);
    axios
      .get(
        //get single coin price
        // "https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd"
        //!has converted prices for coins
        //  'https://api.coingecko.com/api/v3/coins/bitcoin/tickers'
        //!has description of the coin
        //"https://api.coingecko.com/api/v3/coins/bitcoin"
        //!historical price data (for max duration) for a particular coin
        `https://api.coingecko.com/api/v3/coins/${coinLowercase}/market_chart?vs_currency=usd&days=max`
        //list all coins (tens of thousands)
        // "https://api.coingecko.com/api/v3/coins/list"
        //get market cap for a smattering of coins (not sure if all)
        // "https://api.coingecko.com/api/v3/global"
        //have a search bar for individual coins (could show only if it in the list of coins I am displaying), has market cap rank and images links
        // "https://api.coingecko.com/api/v3/search?query=bitcoin"
        //list of supported and currencies (not exactly sure what this means)
        // "https://api.coingecko.com/api/v3/simple/supported_vs_currencies"
      )
      .then((response) => {
        const data = response.data;
        console.log(data);
        const priceData = data.prices;
        const priceDataArray = [];
        priceData.forEach((priceArray) => {
          // console.log(priceArray);
          priceDataArray.push({
            time: priceArray[0],
            //! convert from unix date here?
            value: priceArray[1],
          });
        });
        //!may want to make this (the volume data part)asyncronous (or the whole thing asynchronous), not sure
        const volumeData = data.total_volumes;
        const volumeDataArray = [];
        volumeData.forEach((volumeArray) => {
          // console.log(priceArray);
          volumeDataArray.push({
            time: volumeArray[0],
            //! convert from unix date here?
            value: volumeArray[1],
          });
        });
        console.log(" volume array");
        console.log(volumeDataArray);
        // setCoinData(coinDataArray);
      });
  };

  //colors sparkline chart based on 7d % change
  useEffect(() => {
    if (percentChange7d > 0) {
      setSparklineColor("green");
    } else {
      setSparklineColor("red");
    }
  });

  return (
    <div className="coin-row">
      <div>{rank}</div>
      <div>
        <img src={image} alt="crypto-icon" className="icon"></img>
      </div>
      <NavLink
        to={`/currencies/${name}`}
        key={rank}
        onClick={() => {
          setSelectedCoinData(props.coin);
          retrieveDetailedChartData(name);
        }}
      >
        {name}
      </NavLink>
      <div>{symbol}</div>
      <div>${price}</div>
      <div className="percent-change">{percentChange24hr}</div>
      <div className="percent-change">{percentChange7d}</div>
      <div>${marketCap}</div>
      <div>${volume24hr}</div>
      {/* <div className="chart"></div> */}
      <Sparklines
        // className="sparkline-container"
        data={sparkline}
        // width={100}
        // height={20}
        // margin={5}
      >
        <SparklinesLine
          style={{ strokeWidth: 3, stroke: sparklineColor, fill: "none" }}
        />
      </Sparklines>
    </div>
  );
}
