import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [ethereumPrice, setEthereumPrice] = useState();
  const [lastPrice, setLastPrice] = useState();
  const [greenOrRed, setGreenOrRed] = useState("black");

  let ws = new WebSocket("wss://stream.binance.com:9443/ws/ethusdt@trade");

  ws.onmessage = (event) => {
    let stockObject = JSON.parse(event.data);
    console.log(stockObject.p);
    //convert string price to number(round to two decimal places)
    const shortenedPrice = parseFloat(stockObject.p).toFixed(2);
    setEthereumPrice(shortenedPrice);
  };

  useEffect(() => {
    if (lastPrice <= ethereumPrice) {
      setGreenOrRed("green");
      setLastPrice(ethereumPrice);
    } else {
      setGreenOrRed("red");
      setLastPrice(ethereumPrice);
    }
  }, [ethereumPrice]);

  //GET "/dapi/v1/exchangeInfo"
  // const proxyurl = "https://cors-anywhere.herokuapp.com/";
  // const url = "https://api.binance.us/api/v3/ticker"; // site that doesn’t send Access-Control-*
  // fetch(proxyurl + url)
  //   .then((resp) => resp.json())
  //   .then(function (data) {
  //     console.log(data);
  //   })
  //   .catch(function (error) {
  //     console.log(error);
  //   });

  useEffect(() => {
    axios
      // .get("https://api.binance.us/api/v3/ticker/price?symbol=LTCBTC")
      .get("https://api.binance.us/api/v3/exchangeInfo")
      .then((response) => {
        const data = response.data;
        console.log(data);
      });
  }, []);
  return (
    <div className="App">
      <h1 id="stock-price" className={greenOrRed}>
        {ethereumPrice}
      </h1>
    </div>
  );
}

export default App;
