  // console.log(coinChartData.prices);
  // const priceData = coinChartData.prices;
  // console.log(priceData);

  const retrieveDetailedChartData = (coin) => {
    const coinLowercase = coin.charAt(0).toLowerCase() + coin.slice(1);
    axios
      .get(
        //get single coin price
        // "https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd"
        //!has converted prices for coins
