        //list of supported and currencies (not exactly sure what this means)
        // "https://api.coingecko.com/api/v3/simple/supported_vs_currencies"
      )
      .then((response) => {
        const data = response.data;
        // console.log(data);
        const priceData = data.prices;
        const priceDataArray = [];
        priceData.forEach((priceArray) => {
          // console.log(priceArray);
          priceDataArray.push({
            time: priceArray[0],
            //! convert from unix date here?
            value: priceArray[1],
          });
        });
        //!may want to make this (the volume data part)asyncronous (or the whole thing asynchronous), not sure
        const volumeData = data.total_volumes;
        const volumeDataArray = [];
        volumeData.forEach((volumeArray) => {
          // console.log(priceArray);
          volumeDataArray.push({
            time: volumeArray[0],
            //! convert from unix date here?
            value: volumeArray[1],
          });
        });
        // console.log(" volume array");
        // console.log(volumeDataArray);
        setCoinChartData({ prices: priceDataArray, volume: volumeDataArray });
      });
  };

