import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";

export default function Chart() {
  const isMounted = useRef(false);
  //   const chartRef = React.useRef(null);

  const array = [{ name: "one" }, { name: "two" }, { name: "three" }];

  //   const MakeChart = () => {
  //     const chart = createChart(document.querySelector(".randomNum"), {
  //       width: 400,
  //       height: 300,
  //     });
  //     const lineSeries = chart.addLineSeries();
  //     return lineSeries.setData([
  //       { time: "2019-04-11", value: 80.01 },
  //       { time: "2019-04-12", value: 96.63 },
  //       { time: "2019-04-13", value: 76.64 },
  //       { time: "2019-04-14", value: 81.89 },
  //       { time: "2019-04-15", value: 74.43 },
  //       { time: "2019-04-16", value: 80.01 },
  //       { time: "2019-04-17", value: 96.63 },
  //       { time: "2019-04-18", value: 76.64 },
  //       { time: "2019-04-19", value: 81.89 },
  //       { time: "2019-04-20", value: 74.43 },
  //     ]);
  //   };

  const coinList = array.map((coin) => {
    const randomNum = Math.floor(Math.random() * 10000);
    return (
      <div>
        <div>{coin.name}</div>
        <div className={randomNum}></div>
        {/* {<MakeChart />} */}
      </div>
    );
  });

  useEffect(() => {
    if (isMounted.current) {
      const chart = createChart(document.querySelector(".chart"), {
        width: 400,
        height: 300,
      });
      const lineSeries = chart.addLineSeries();
      lineSeries.setData([
        { time: "2019-04-11", value: 80.01 },
        { time: "2019-04-12", value: 96.63 },
        { time: "2019-04-13", value: 76.64 },
        { time: "2019-04-14", value: 81.89 },
        { time: "2019-04-15", value: 74.43 },
        { time: "2019-04-16", value: 80.01 },
        { time: "2019-04-17", value: 96.63 },
        { time: "2019-04-18", value: 76.64 },
        { time: "2019-04-19", value: 81.89 },
        { time: "2019-04-20", value: 74.43 },
      ]);
    } else {
      isMounted.current = true;
    }
  });

  return (
    <div>
      <div>Chart</div>
      <div className="chart"></div>
      {coinList}
      <div>below chart</div>
    </div>
  );
}
