import React, { useEffect, useRef, useContext, useState } from "react";
import Context from "../context/context";
import { createChart } from "lightweight-charts";
import CoinRow from "./CoinRow";
import axios from "axios";
import "./coinlist.css";

export default function Coinlist() {
  const isMounted = useRef(false);
  const { coinData } = useContext(Context);
  const [coinDataForRows, setCoinDataForRows] = useState();
  //   {
  //     name: "Bitcoin",
  //     price: "5000",
  //     percentChange: "5",
  //     data: [
  //       { time: "2019-04-11", value: 80.01 },
  //       { time: "2019-04-12", value: 96.63 },
  //       { time: "2019-04-13", value: 76.64 },
  //       { time: "2019-04-14", value: 80.64 },
  //       { time: "2019-04-15", value: 40.64 },
  //       { time: "2019-04-16", value: 67.64 },
  //       { time: "2019-04-17", value: 57.64 },
  //     ],
  //   },
  // ]);

  // console.log(coinData);

  // const {
  //   rank,
  //   image,
  //   name,
  //   symbol,
  //   price,
  //   percentChange24hr,
  //   percentChange7d,
  //   marketCap,
  //   volume24hr,
  //   sparkline,
  // } = coinData;
  useEffect(() => {
    setCoinDataForRows(coinData);
  }, [coinData]);
  return (
    <div className="coinlist-container">
      <div>
        Top Polkadot Ecosystem Tokens by Market Capitalization This page lists
        the most valuable Polkadot based coins and tokens. These projects are
        listed by market capitalization with the largest first and then
        descending in order.
      </div>
      <div className="coinlist-table-headers">
        <div>Rank</div>
        <div>Name</div>
        <div>Price</div>
        <div>24hr%</div>
        <div>7d%</div>
        <div>Market Cap</div>
        <div>Volume(24hr)</div>
        <div>Last 7 Days</div>
      </div>
      {coinData.map((coin) => {
        return <CoinRow coin={coin} />;
      })}
    </div>
  );
}
