import React, { useContext, useState, useEffect } from "react";
import Context from "../context/context";

export default function Chart() {
  const { coinChartData } = useContext(Context);
  const [data, setData] = useState();
  const isMounted = useRef(false);

  console.log("coinChartData");
  console.log(coinChartData);

  useEffect(() => {
    setData(coinChartData);
    // console.log(data.prices);
  }, [coinChartData]);
  // console.log(coinChartData.prices);
  // const priceData = coinChartData.prices;
  // console.log(priceData);

  useEffect(() => {
    if (isMounted.current) {
      const chart = createChart(document.querySelector(".chart"), {
        width: 100,
        height: 75,
        rightPriceScale: {
          visible: false,
        },
        grid: {
          vertLines: {
            visible: false,
          },
          horzLines: {
            visible: false,
          },
        },
        crosshair: {
          vertLine: {
            visible: false,
          },
          horzLine: {
            visible: false,
          },
        },
        layout: {
          backgroundColor: "tan",
        },
        timeScale: { visible: false },
        // borderColor: "green",
        // timeVisible: false,
        // priceScaleMode: percentChange,
      });
      //fits timescale to the content
      chart.timeScale().fitContent();
      const lineSeries = chart
        .addLineSeries
        //used to set y-axis scale (not quite sure exactly how it works)
        //   {
        //   autoscaleInfoProvider: () => ({
        //     priceRange: {
        //       minValue: 0,
        //       maxValue: 100,
        //     },
        //   }),
        // }
        ();
      lineSeries.applyOptions({
        color: "red",

        lineWidth: 5,
        //crosshair dot
        crosshairMarkerVisible: false,
        //both of these are similar
        lastValueVisible: false,
        priceLineVisible: false,
        //can make the series disapper (labels remain)
        visible: true,
        //
      });
      // const customFormatter = (time, tickMarkType, locale) => {
      //   // tickMarkType: "year";
      // };
      lineSeries.setData(data);
      //   window.addEventListener("resize", handleResize);
    } else {
      isMounted.current = true;
    }
  });

  return <div>Chart</div>;
}
