import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [ethereumPrice, setEthereumPrice] = useState();
  const [lastPrice, setLastPrice] = useState();
  const [greenOrRed, setGreenOrRed] = useState();

  let ws = new WebSocket("wss://stream.binance.com:9443/ws/etheur@trade");

  ws.onmessage = (event) => {
    let stockObject = JSON.parse(event.data);
    console.log(stockObject.p);
    //convert string price to number(round to two decimal places)
    const shortenedPrice = parseFloat(stockObject.p).toFixed(2);
    setEthereumPrice(shortenedPrice);
  };

  useEffect(() => {
    if (lastPrice < ethereumPrice) {
      console.log("green");
      setGreenOrRed("green");
      setLastPrice(ethereumPrice);
    } else {
      console.log("red");
      setGreenOrRed("red");
      setLastPrice(ethereumPrice);
    }
  }, [ethereumPrice]);

  return (
    <div className="App">
      <h1 id="stock-price" className={greenOrRed}>
        {ethereumPrice}
      </h1>
    </div>
  );
}

export default App;
