import React from "react";

export default function ConditionalRenderList({
  value,
  list,
  setValue,
  toggle,
  setToggle,
}) {
  //if user enters value in search bar
  if (value) {
    const filteredList = list.filter((item) => {
      item.data.toString().toLowerCase().startsWith(value.toLowerCase());
    });

    if (filteredList.length) {
      return (
        toggle &&
        filteredList.map((item) => {
          <div>
            <div
              onClick={() => {
                setToggle(false);
                setValue(item.data);
              }}
            >
              {item.data}
            </div>
          </div>;
        })
      );
    }
  }

  return <div>ConditionalRenderList</div>;
}
