import React, { useEffect, useRef } from "react";
import { createChart } from "lightweight-charts";

export default function CoinRow(props) {
  const isMounted = useRef(false);
  console.log(props);
  const { name, price, percentChange, data } = props.coin;

  useEffect(() => {
    if (isMounted.current) {
      const chart = createChart(document.querySelector(".chart"), {
        width: 400,
        height: 300,
      });
      //fits timescale to the content
      chart.timeScale().fitContent();
      const lineSeries = chart
        .addLineSeries
        //used to set y-axis scale (not quite sure exactly how it works)
        //   {
        //   autoscaleInfoProvider: () => ({
        //     priceRange: {
        //       minValue: 0,
        //       maxValue: 100,
        //     },
        //   }),
        // }
        ();
      lineSeries.applyOptions({
        color: "red",
        lineWidth: 5,
        //crosshair dot
        crosshairMarkerVisible: true,
        //both of these are similar
        lastValueVisible: false,
        priceLineVisible: false,
        //can make the series disapper (labels remain)
        visible: true,
      });
      lineSeries.setData(data);
      window.addEventListener("resize", handleResize);
    } else {
      isMounted.current = true;
    }
  });

  return (
    <div key={name}>
      <div>{name}</div>
      <div>{price}</div>
      <div>{percentChange}</div>
      <div className="chart"></div>
    </div>
  );
}
